function normlizePixel(value){
    return value/255.0;
}

function predictImage(){
    console.log("Processing...");

    let image = cv.imread(canvas);
    var expCanvas = document.createElement("CANVAS");

    // Display the image on the webpage for experiment
    //cv.imshow(expCanvas, image);
    //document.body.appendChild(expCanvas);
 
    // You can try more different parameters
    cv.cvtColor(image, image, cv.COLOR_RGBA2GRAY, 0);
    cv.threshold(image, image, 175, 254, cv.THRESH_BINARY);// Any pixel above 175 is changed to whilte, all others suppressed
    let contours = new cv.MatVector();
    let hierarchy = new cv.Mat();
    // You can try more different parameters
    cv.findContours(image, contours, hierarchy, cv.RETR_CCOMP, cv.CHAIN_APPROX_SIMPLE);

    let cnt = contours.get(0);
    // You can try more different parameters
    let rect = cv.boundingRect(cnt); // Bound the image within a rectangle
    image = image.roi(rect); // Crop the image within the rectangle

    imwidth = image.cols;
    imheight = image.rows;
    let scalingFactor = 0;

    if (imheight > imwidth){
        //calculat scaling factor based on height
        imwidth = Math.round((20.0 * imwidth)/imheight);
        imheight = 20.0;
    }else{
        imheight = Math.round((20.0 * imheight)/imwidth);
        imwidth = 20.0;
    }
    //console.log(`imheight= ${imheight}, imwidth=${imwidth}`);
    let dsize = new cv.Size(imwidth, imheight);
    // You can try more different parameters
    cv.resize(image, image, dsize, 0, 0, cv.INTER_AREA);

    // Not he image is 20X20
    // We need to make it 28X28 to feed into TF
    // Add 4 pixels of padding on left right top bottom
    const LEFT=4 +Math.ceil((20 - image.cols)/2);
    const RIGHT=4 +Math.floor((20 - image.cols)/2);
    const TOP=4 +Math.ceil((20 - image.rows)/2);
    const BOTTOM=4 +Math.floor((20 - image.rows)/2);
    let s = new cv.Scalar(0, 0, 0, 0);
    cv.copyMakeBorder(image, image, TOP, BOTTOM, LEFT, RIGHT, cv.BORDER_CONSTANT, s);

    // Figure out the x and y coordinate of the center of mass
    cv.findContours(image, contours, hierarchy, cv.RETR_CCOMP, cv.CHAIN_APPROX_SIMPLE);
    cnt = contours.get(0);
    let moments = cv.moments(cnt,false);
    let com_x =  moments.m10/moments.m00;
    let com_y =  moments.m01/moments.m00;

    // We need to shift the image so that the center of mass is at thr midpoint
    // its a 28X28 image, ideally the center of mass needs to be be at (14,14)
    // So we need to offset the image by com_x and com_y above
    const x_shift = Math.round(image.cols/2 - com_x);
    const y_shift = Math.round(image.rows/2 - com_y); 
    //console.log(`x_shift:${x_shift}, y_shift:${y_shift}`);

    let M = cv.matFromArray(2, 3, cv.CV_64FC1, [1, 0, x_shift, 0, 1, y_shift]);
    dsize = new cv.Size(image.rows, image.cols);
    // You can try more different parameters
    cv.warpAffine(image, image, M, dsize, cv.INTER_LINEAR, cv.BORDER_CONSTANT, s);

    // Normalise the pixel values
    var pixelV = image.data;
    // Convert to float array as image.data returns integer
    pixelV = Float32Array.from(pixelV);
    pixelV = pixelV.map(normlizePixel);

    // Create a tensor from pixel values
    tensorToFeed = tf.tensor([pixelV]);

    // May print shape here to verify whether its (1,784)
    //console.log(`Tensor shape: ${tensorToFeed.shape}`);

    // Make a prediction
    let result = model.execute({ 'X': tensorToFeed }, ['prediction']);
    result.print();

    const output = result.dataSync();

    tensorToFeed.dispose();
    result.dispose();
    cnt.delete();contours.delete(); hierarchy.delete(); image.delete(); M.delete();

    return output[0];


}
